import React,{useState} from "react";
import ProductsJson from "../js/data.json";
import { Card } from "react-bootstrap";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Col from 'react-bootstrap/Col';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Row from 'react-bootstrap/Row';
import "../App.css";
import "../index";
import yeah from '../images/search.png'
function ProductsData() {
  const [searchList, setSearchList] = useState("")
  console.log(searchList)
  return (
    
    <>
      
      <Row className="g-2 mt">
      <div className="col-lg-4 col-md-4 col-sm-12 col-10 d-block m-auto ">
          <div style={{ display: "inline-block", margin: "3rem 0rem" }}>
            <select className="select  mx-2" data-mdb-filter="true">
              <option value="1">Availability</option>
              <option value="2">Only in stock</option>
            </select>
            <select className="select mx-2" data-mdb-filter="true">
              <option value="1">Price</option>
            </select>
            <select className="select  mx-2" data-mdb-filter="true">
              <option value="1">Tag</option>
              <option value="2">Old</option>
              <option value="3">Prints</option>
              <option value="4">Waterclour</option>
            </select>
          </div>
        </div>
      <Col  className="col-lg-2 col-md-5 col-sm-5 col-7 m-auto  mt-5">
      <InputGroup  className=" input " >
                <Form.Control
                  placeholder="Recipient's username"
                  aria-label="Recipient's username"
                  aria-describedby="basic-addon2"
                  onChange={(e)=>setSearchList(e.target.value)}
                />
                <InputGroup.Text id="basic-addon2"><img src={yeah} alt='icon'/></InputGroup.Text>
              </InputGroup>
      </Col>
      <Col className="col-lg-2 col-md-5 col-sm-5 col-7 m-auto  mt-5">
        <FloatingLabel
          controlId="floatingSelectGrid"
          label="Works with selects"
        >
          <Form.Select aria-label="Floating label select example"  className=" input ">
            <option>Open this select menu</option>
            <option value="1">One</option>
            <option value="2">Two</option>
            <option value="3">Three</option>
          </Form.Select>
        </FloatingLabel>
      </Col>
    </Row>
      
            
            
              
           
        
        
        
              <div className="row ms-4 mx-5">
        {ProductsJson.filter((val)=> {
          if(searchList === " "){
            return val
          }else if (val.title.toLowerCase().includes(searchList.toLowerCase())){
            return val
          }
        }).map((data) => (
          <div className="col-lg-3 col-md-3 col-sm-12 col-10 mx-auto mt-4">
            <Card style={{ width: "18rem", border: "none" }} key={data.id}>
              <Card.Img
                variant="top"
                src={data.image}
                style={{ height: "20rem" }}
                className="mainImage"
              />
              <div className="card-block">
                <Card.Body className="text-center">
                  <h6
                    style={{
                      fontSize: "1.1rem",
                      fontFamily: "FuturaPT,Arial,sans-serif",
                    }}
                  >
                    {data.title}
                  </h6>
                  <Card.Text
                    style={{
                      fontSize: "0.86rem",
                      fontFamily: "FuturaPT,Arial,sans-serif",
                    }}
                  >
                    {data.price} usd
                  </Card.Text>
                </Card.Body>
              </div>
            </Card>
          </div>
        ))}
      </div>
    </>
  );
}

export default ProductsData;
