import React,{useState} from "react";
import ProductsJson from "../js/data.json";
import { Card } from "react-bootstrap";
import Form from "react-bootstrap/Form";
import InputGroup from "react-bootstrap/InputGroup";
import Col from 'react-bootstrap/Col';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Row from 'react-bootstrap/Row';
import "../App.css";
import "../index";
import yeah from '../images/search.png'
function ProductsData() {
  const [searchList, setSearchList] = useState("")
  console.log(searchList)
  return (
    
    <>
      
      <Row className=" gx-5 mt-5">
      <div className="col-lg-4 col-md-4 col-sm-12 col-10 d-block m-auto  ms-5 ">
          <div >
            <select className="form-select-sm select" >
              <option >Availability</option>
              <option value="2">Only in stock</option>
            </select>
            <select className=" form-select-sm select">
              <option value="1">Price</option>
            </select>
            <select className="form-select-sm select">
            
              <option value="1">Tag</option>
              <option value="2">Old</option>
              <option value="3">Prints</option>
              <option value="4">Waterclour</option>
             
            </select>
          </div>
        </div>
      <Col  className="col-lg-2 col-md-3 col-sm-5 col-5 m-auto me-5">
      <InputGroup  className="input ms-5 h-100  " >
                <Form.Control
                  placeholder="Search"
                  aria-label="Search"
                 className="input"
                  onChange={(e)=>setSearchList(e.target.value)}
                />
                <InputGroup.Text id="basic-addon2"className="group" ><img src={yeah} alt='icon'style={{width:'15px'}}/></InputGroup.Text>
              </InputGroup>
      </Col>
      <Col className="col-lg-2 col-md-3 col-sm-5 col-5 m-auto ms-2 ">
          <Form.Select  className="input secound">
            <option className="p-3">Sort: by default</option>
            <option value="1">Price: low to high</option>
            <option value="2">Price:  high to low</option>
            <option value="3">Title: A--Z</option>
            <option value="3">Title: Z--A</option>
            <option value="3">Sort: newest first</option>
            <option value="3">Sort: oldest first</option>


          </Form.Select>
       
      </Col>
    </Row>
              <div className="row ms-4 mx-5">
        {ProductsJson.filter((val)=> {
          if(searchList === " "){
            return val
          }else if (val.title.toLowerCase().includes(searchList.toLowerCase())){
            return val
          }
        }).map((data) => (
          <div className="col-lg-3 col-md-3 col-sm-12 col-10 mx-auto mt-4">
            <Card style={{ width: "18rem", border: "none" }} key={data.id}>
              <Card.Img
                variant="top"
                src={data.image}
                style={{ height: "20rem" }}
                className="mainImage"
              />
              <div className="card-block">
                <Card.Body className="text-center">
                  <h6
                    style={{
                      fontSize: "1.1rem",
                      fontFamily: "FuturaPT,Arial,sans-serif",
                    }}
                  >
                    {data.title}
                  </h6>
                  <Card.Text
                    style={{
                      fontSize: "0.86rem",
                      fontFamily: "FuturaPT,Arial,sans-serif",
                    }}
                  >
                    {data.price} usd
                  </Card.Text>
                </Card.Body>
              </div>
            </Card>
          </div>
        ))}
      </div>
    </>
  );
}

export default ProductsData;
