import React from "react";
import ProductsJson from "../js/data.json";
import { Card } from "react-bootstrap";
import "../App.css";
function ProductsData() {
  return (
    <>
      <div className="row ms-4 mx-5">
        {ProductsJson.map((data) => (
          <div className="col-lg-3 col-md-3 col-sm-12 col-10 mx-auto mt-4">
            <Card style={{ width: "18rem", border: "none" }} key={data.id}>
              <Card.Img
                variant="top"
                src={data.image}
                style={{ height: "20rem" }}
                className="mainImage"
              />
              <div className="card-block">
                <Card.Body className="text-center">
                  <h6
                    style={{
                      fontSize: "1.1rem",
                      fontFamily: "FuturaPT,Arial,sans-serif",
                    }}
                  >
                    {data.title}
                  </h6>
                  <Card.Text
                    style={{
                      fontSize: "0.86rem",
                      fontFamily: "FuturaPT,Arial,sans-serif",
                    }}
                  >
                    {data.price} usd
                  </Card.Text>
                </Card.Body>
              </div>
            </Card>
          </div>
        ))}
      </div>
    </>
  );
}

export default ProductsData;
